import React, { useState } from 'react';

const Tour = ({id,image,info,price,name}) => {
  return <article className='single-tour'>
<img src={image} alt={name}></img>
<footer>
  <div className='tour-info'>
    <h4>{name}</h4>
    <h4 className='tour-price'>${price}</h4>
  </div>
</footer>
  </article>;
};

export default Tour;
